#include <iostream>
#include <fstream>
#include <string>

using namespace std;

int main() {

	//파일 생성
	char buf[20000];
	ofstream fout("data.bin",ios::out|ios::binary);
	char str[] = { "" };
	fout.write(str, sizeof(str) - 1);
	fout.close();

	ifstream fin("data.bin", ios::in | ios::binary);
	fin.seekg(0, ios::end);
	int sz = fin.tellg();
	fin.seekg(0, ios::beg);

	int size = 0;
	cin >> size;
	int size_divide = sz / size;

	for (int i = 0; i <= size_divide; i++) {
		string filename = "out.d" + to_string(i);
		ofstream fout(filename, ios::out | ios::binary);
		if (i == size_divide) {
			fin.read(buf, sz%size);
			fout.write(buf, sz%size);
		}
		else {
			fin.read(buf, size);
			fout.write(buf, size);
		}
		fout.close();
	}
	system("pause");
	return 0;

}